jQuery(function() {
	initCustomForms();
	initResizebleBlocks();
	initAddPanel();
	initRefreshScroll();
});

function initRefreshScroll() {
	jQuery('.jcf-scrollable').on('click', 'a', function() {
		var scrollAPI = jQuery(this).closest('.jcf-scrollable').data('jcfInstance');
		if (scrollAPI) {
			setTimeout(function() {
				scrollAPI.refresh();
			}, 200);
		}
	});
}

function initAddPanel() {
	var activeClass = 'active';
	var win = jQuery(window);
	jQuery('.panel-opener').each(function() {
		var opener = jQuery(this);
		var target = jQuery(opener.data('target'));
		var close = target.find('.close');
		activeClass
		opener.on('click', function(e) {
			e.preventDefault();
			target.toggleClass(activeClass);
			win.trigger('resize');
		});
		close.on('click', function(e) {
			e.preventDefault();
			target.removeClass(activeClass);
		});
	});
}

// initialize custom form elements
function initCustomForms() {
	jcf.replaceAll();
}

function initResizebleBlocks() {
	var resizeClass = 'resize-active';
	jQuery('.blocks-holder').each(function() {
		var holder = jQuery(this);
		var resizeBlock = holder.find('.resizable-block');
		var bindBlock = holder.find(resizeBlock.data('bind'));

		// set size in percentage
		function setSize() {
			var nawSize = resizeBlock.height() * 100 / holder.height();
			var bindSize = 100 - nawSize;
			resizeBlock.css('height', nawSize + '%');
			bindBlock.css('height', bindSize + '%');
		}
		resizeBlock.resizable({
			resize: setSize,
			create: setSize,
			start: function() {
				if (!holder.hasClass(resizeClass)) holder.addClass(resizeClass);
			},
			stop: function() {
				if (holder.hasClass(resizeClass)) holder.removeClass(resizeClass);
			}
		});
	});
}

